mapper.select:
SELECT jobExecutionId , version , jobInstanceId , jobName , createTime , startTime , endTime , status , exitCode , exitMessage , lastUpdated , jobConfigurationLocation FROM ( SELECT @rownum:=@rownum+1 AS rnum , jobExecutionId , version , jobInstanceId , jobName , createTime , startTime , endTime , status , exitCode , exitMessage , lastUpdated , jobConfigurationLocation FROM ( SELECT exe.job_execution_id AS jobExecutionId , exe.version AS version , exe.job_instance_id AS jobInstanceId , inst.job_name AS jobName , exe.create_time AS createTime , exe.start_time AS startTime , exe.end_time AS endTime , exe.status AS status , exe.exit_code AS exitCode , exe.exit_message AS exitMessage , exe.last_updated AS lastUpdated , exe.job_configuration_location AS jobConfigurationLocation FROM ${tablePrefix}_JOB_INSTANCE inst , ${tablePrefix}_JOB_EXECUTION exe , (SELECT @rownum := 0) R WHERE inst.job_instance_id = exe.job_instance_id AND inst.job_name IN
#{jobName}
ORDER BY exe.job_execution_id DESC ) ) WHERE rnum BETWEEN #{start} AND (#{start} + #{length})
